tr>                        <td><?=$commande['temp']?></td>                        <td><?=$commande['id_commande']?></td>                        <td><?=$commande['nom']?></td>                        <td>0<?=$commande['telephone']?></td>                        <?php                             $sqlWilaya = $pdo->prepare('SELECT wilaya FROM delivery WHERE id=?');                            $sqlWilaya->execute([$commande['wilaya']]);                            $wilaya = $sqlWilaya->fetch(PDO::FETCH_ASSOC);                        ?>                        <td><?=$wilaya['wilaya']?></td>                        <td><?=$commande['commune']?></td>                        <td><?=$commande['livraison']?></td>                        <?php                            $sqlPanier = $pdo->prepare('SELECT * FROM panier WHERE id_session=? AND status=true');                            $sqlPanier->execute([$commande['id_session']]);                            $nbCommande = $sqlPanier->rowCount();                        ?>                        <td><?=$nbCommande?></td>                        <td><a href="detail.php?id=<?=$commande['id_commande']?>" id="modCom"><i class="fa-solid fa-circle-info"></i></a></td>                        <td><a href="supprimer/sup-commande.php?id=<?=$commande['id_commande']?>" class="btn-sup"><i class="fa-solid fa-trash"></i></a></td>                    </tr>                <?php } ?>                </tbody>            </table>        </div>    </div></body></html>