ead>                    <tr>                        <th>Temp</th>                        <th>Id</th>                        <th>Nom</th>                        <th>Telephone</th>                        <th>Email</th>                        <th>Message</th>                    </tr>                </thead>                <tbody>                <?php                    $sqlStates = $pdo->prepare('SELECT * FROM contact ORDER BY id_contact DESC');                    $sqlStates->execute();                    $clients = $sqlStates->fetchAll(PDO::FETCH_ASSOC);                    foreach($clients as $client){                ?>                    <tr>                        <td><?=$client['date_creation']?></td>                        <td><?=$client['id_contact']?></td>                        <td><?=$client['nom']?></td>                        <td>0<?=$client['tel']?></td>                        <td><?=$client['email']?></td>                        <td><?=nl2br($client['message'])?></td>                        <td><a href="supprimer/sup-contact.php?id=<?=$client['id_contact']?>" class="btn-sup"><i class="fa-solid fa-trash"></i></a></td>                    </tr>                <?php } ?>                </tbody>            </table>        </div>    </div>    </div></body></html>