<!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <link rel="stylesheet" href="css/style.css">    <title>Dashboard</title>    <link rel="preconnect" href="https://fonts.googleapis.com">    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>    <link href="https://fonts.googleapis.com/css2?family=Josefin+Sans:wght@300&family=Oswald&family=Pacifico&family=Roboto&family=Roboto+Slab:wght@300&display=swap" rel="stylesheet">    <script src="https://kit.fontawesome.com/abbd21db44.js" crossorigin="anonymous"></script>    <script src="https://unpkg.com/sweetalert/dist/sweetalert.min.js"></script></head><body>    <?php     ob_start();        session_start();        if(!isset( $_SESSION['utilisateur'])){            header('location:connexion.php');            exit;        }        require_once('database.php');        include('include/menu.php');        $sqlStates = $pdo->prepare('SELECT * FROM setting WHERE id=?');        $sqlStates->execute([1]);        $setting = $sqlStates->fetch(PDO::FETCH_ASSOC);         if(isset($_GET['added'])){            ?>            <script>            swal({                title: "Modification avec succes !",                text: "Vos informations ont ete bien modifie !",                icon: "success",            });        </script><?php         }     ?>    <div class="site">        <div class="barre">Parametre</div>        <form method="POST" enctype="multipart/form-data">        <table class="setting">            <tr>                <td>Nom de la boutique</td>                <td><input type="text" name="nom" value="<?=$setting['nom']?>" placeholder="Nom de la boutique"></td>            </tr>            <tr>                <td>Numero de telephone</td>                <td><input type="tel"  name="tel" value="0<?=$setting['tel']?>" placeholder="05..." maxlength="10"></td>            </tr>            <tr>                <td>Adresse</td>                <td><input type="text" name="adresse" value="<?=$setting['adresse']?>" placeholder="Wilaya commune"></td>            </tr>            <tr>                <td>Facebook</td>                <td><input type="text" name="facebook" value="<?=$setting['facebook']?>" placeholder="https://facebook.com"></td>            </tr>            <tr>                <td>Instagram</td>                <td><input type="text" name="insta" value="<?=$setting['insta']?>" placeholder="https://instagram.com"></td>            </tr>            <tr>                <td>Tiktok</td>                <td><input type="text" name="tiktok" value="<?=$setting['tiktok']?>" placeholder="https://tiktok.com"></td>            </tr>                        <tr>                <td>Image</td>                <td><input type="file" name="image" value="<?=$setting['image']?>"></td>            </tr>        </table>        <input type="submit" name="enregistrer" value="enregistrer" id="enregistrer">        </form>        <?php            if(isset($_POST['enregistrer'])){                $nom = $_POST['nom'];                $tel = $_POST['tel'];                $adresse = $_POST['adresse'];                $facebook = $_POST['facebook'];                $insta = $_POST['insta'];                $tiktok = $_POST['tiktok'];                function uploadImage($inputName) {                    $file = $_FILES[$inputName];                                        $fileName = $file['name'];                    $fileTmpName = $file['tmp_name'];                    // Si le fichier est vide, retourne une chaÃ®ne vide                    if (empty($fileName)) {                        return '';                    }                    $fileExt = strtolower(pathinfo($fileName, PATHINFO_EXTENSION));                    $uniqueName = uniqid('', true) . '.' . $fileExt;                    $fileDestination = '../img/' . $uniqueName;                    move_uploaded_file($fileTmpName, $fileDestination);                    return $uniqueName;                }                $image = uploadImage('image');                $updateImg = !empty($_FILES['image']['name']);                $sqlModifier = 'UPDATE setting SET nom=?,tel=?,adresse=?,facebook=?,insta=?,tiktok=?';                $params = [$nom,$tel,$adresse,$facebook,$insta,$tiktok];                if ($updateImg) {                    $sqlModifier .= ', image=?';                    $params[] = $image;                }                $sql = $pdo->prepare($sqlModifier);                $sql->execute($params);                                header('location:setting.php?added=succes');                ob_end_flush();            }        ?>    </div></body></html>